# -------------------------------------------------------------------------------# Description: 通过词频生成词云图# Reference:# Author: hanhan# Date: 2022/2/26# -------------------------------------------------------------------------------import wordcloudimport PIL.Image as imageimport numpy as npfrom matplotlib import colorsfrom Configs.config import TermFrequencyText, WordCloudBackgroundPath, OutputWordCloudPath# 根据背景图片绘制词云图def ImgDraw():    print('正在绘制词云图...')    f = open(TermFrequencyText, encoding='utf-8')    txt = f.read()    img = np.array(image.open(WordCloudBackgroundPath))    color_list = ['#A0BF7C', '#65934A', '#407434']  # 建立颜色数组    colormap = colors.ListedColormap(color_list)  # 调用    w = wordcloud.WordCloud(width=1000,                            height=700,                            background_color='white',                            mask=img,                            colormap=colormap,                            font_path='msyh.ttc')    w.generate(txt)    w.to_file(OutputWordCloudPath)    f.close()    print('词云图保存成功')if __name__ == '__main__':    ImgDraw()