"""@Description : 词云绘制@File        : WordCount.py@Project     : TextAnalysisTools@Time        : 2022/3/28 11:13@Author      : hanhan@Software    : PyCharm"""import wordcloudimport PIL.Image as imageimport numpy as npfrom matplotlib import colorsdef ImgDraw(inputUrl, outputUrl, imgUrl=''):    """    根据背景图片绘制词云图    :param inputUrl: 输入的词频文件    :param outputUrl: 输出的图片文件    :param imgUrl: 输入的背景图片（可以不输入）    :return:    """    print('正在绘制词云图...')    f = open(inputUrl, encoding='utf-8')    txt = f.read()    img = None    try:        img = np.array(image.open(imgUrl))    except:        pass    color_list = ['#A0BF7C', '#65934A', '#407434']  # 建立颜色数组    colormap = colors.ListedColormap(color_list)  # 调用    w = wordcloud.WordCloud(width=1000,                            height=700,                            background_color='white',                            mask=img,                            colormap=colormap,                            font_path='msyh.ttc')    w.generate(txt)    w.to_file(outputUrl)    f.close()    print('词云图保存成功')if __name__ == '__main__':    pass